import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // Aggiunto import di useNavigate
import { useAuth } from "../util/AuthContext";
import "./Login.css";

function Login() {
  const auth = useAuth();
  const { user, errore } = useAuth();
  const navigate = useNavigate(); // Inizializzato useNavigate
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    if (user) {
      navigate("/");
    }
  }, [user, navigate]);

  async function handleLogin() {
    console.log(email);
    console.log(password);
    try {
      await auth.loginUser(email, password);
      navigate("/"); // Naviga solo dopo il login con successo
    } catch (error) {
      // Puoi gestire gli errori qui, se necessario
    }
  }

  return (
    <div className="page-wrapper">
      <p style={{ color: "red" }}>{errore.message}</p>
      <div class="oaerror danger">
        <strong>Error</strong>
        {errore.message}
      </div>
      <h1>Login</h1>
      <input
        type="email"
        placeholder="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      ></input>
      <input
        type="password"
        placeholder="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      ></input>
      <button className="login-button" onClick={handleLogin}>
        LOGIN
      </button>
    </div>
  );
}

export default Login;
