import React, { createContext, useContext, useEffect, useState } from "react";

const AuthContext = createContext();

const appwrite = new Appwrite();

// Imposta l'endpoint e l'ID progetto di Appwrite
appwrite.setEndpoint('YOUR_APPWRITE_ENDPOINT');
appwrite.setProject('YOUR_APPWRITE_PROJECT_ID');

export const AuthProvider = ({ children }) => {
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Al montaggio del componente, verifica lo stato di autenticazione
    checkUserStatus();
  }, []);

  const loginUser = async (email, password) => {
    try {
      // Chiamata a Appwrite per effettuare il login
      const response = await appwrite.account.createSession(email, password);
      setUser(response);
      setLoading(false);
    } catch (error) {
      console.error("Errore durante il login:", error);
    }
  };

  const logoutUser = async () => {
    try {
      // Chiamata a Appwrite per effettuare il logout
      await appwrite.account.deleteSession("current");
      setUser(null);
      setLoading(false);
    } catch (error) {
      console.error("Errore durante il logout:", error);
    }
  };

  const registerUser = async (email, password) => {
    try {
      // Chiamata a Appwrite per registrare un nuovo utente
      const response = await appwrite.account.create(email, password);
      setUser(response);
      setLoading(false);
    } catch (error) {
      console.error("Errore durante la registrazione:", error);
    }
  };

  const checkUserStatus = async () => {
    try {
      // Chiamata a Appwrite per ottenere lo stato dell'utente corrente
      const response = await appwrite.account.get();
      setUser(response);
      setLoading(false);
    } catch (error) {
      console.error("Nessun utente autenticato:", error);
      setLoading(false);
    }
  };

  const contextData = {
    user,
    loginUser,
    logoutUser,
    registerUser,
    checkUserStatus,
  };

  return (
    <AuthContext.Provider value={contextData}>
      {loading ? <p>Caricamento...</p> : children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
export default AuthContext;
