import React, { useEffect, useState } from "react";
import { account } from "../appwriteConfig";
import "./Login.css";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../util/AuthContext";
function Login() {
  const {auth,error} = useAuth(); // Utilizza useAuth per ottenere accesso al contesto
  const { user } = useAuth();
  useEffect(() => {
    if (user) {
      navigate("/");
    }
  }, []);
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const navigate = useNavigate();

  async function handleLogin() {
    console.log(email);
    console.log(password);
    try {
      // Chiamata alla funzione loginUser utilizzando il contesto
      await auth.loginUser(email, password);
      // Qui puoi aggiungere la navigazione a una nuova pagina o eseguire altre azioni dopo il login
      navigate("/");
    } catch (error) {
      console.error("Errore durante il login:", error);
    }
  }

  return (
    <div className="page-wrapper">
      <h1>Login</h1>
      <input
        type="email"
        placeholder="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      ></input>
      <input
        type="password"
        placeholder="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      ></input>
      <button className="login-button" onClick={handleLogin}>
        LOGIN
      </button>
    </div>
  );
}

export default Login;
